import React from 'react';
import styled from 'styled-components'
import { Parallax, Background } from 'react-parallax';


const Head = styled.div`
    display: flex;
    flex-direction: column;
    text-align: center;
`

const About = () => {
    return <div>
    {/* -----basic config-----*/}
    <Parallax
        blur={10}
        bgImage={'https://images.unsplash.com/photo-1542044896530-05d85be9b11a?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=925&q=80'}
        bgImageAlt="the cat"
        strength={200}
    >
        Dawei Liu
        <div style={{ height: '500px' }} />
    </Parallax>

    {/* -----dynamic blur-----*/}
    <Parallax
        blur={{ min: -15, max: 15 }}
        bgImage={'./photos/take_flight/swans.jpg'}
        bgImageAlt="the dog"
        strength={-200}
    >
        Blur transition from min to max
        <div style={{ height: '500px' }} />
    </Parallax>

    {/* -----custom background element-----*/}
    <Parallax strength={300}>
        <div>Use the background component for custom elements</div>
        <Background className="custom-bg">
            <img src="./photos/take_flight/swans.jpg" alt="fill murray" />
        </Background>
    </Parallax>

    {/* -----renderProp: "renderLayer"-----*/}
    <Parallax
        bgImage={'./photos/take_flight/swans.jpg'}
        strength={400}
        renderLayer={percentage => (
            <div
                style={{
                    position: 'absolute',
                    background: `rgba(255, 125, 0, ${percentage * 1})`,
                    left: '50%',
                    top: '50%',
                    width: percentage * 500,
                    height: percentage * 500,
                }}
            />
        )}
    >
        <p>... Content</p>
    </Parallax>
</div>
}

export default About
